package studentdata;

public class Calculator {
	
	static int a=10;
	static int b=20;
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double result=0;
		result=a/b;
		System.out.println(result);
		
		double addresult=a+b;
		System.out.println(addresult);
		
		int subresult=a-b;
		System.out.println(subresult);

	}

}
